mapper.insert:
insert into tb_member(
	member_no, id, pass, email, name
) values (
	s_member_no.nextval, #{id}, #{pass}, 
	#{email}, #{name}
)

mapper.select:
select count(*)
  from tb_member
 where id = #{id}

select count(*)
  from tb_member
 where email = #{email}

select *
  from tb_member
 where id = #{id}
   and pass = #{pass}

select id, name, email
  from tb_member
 where id = #{id}

mapper.update:
update tb_member
   set pass = #{pass}
 where id = #{id}

mapper.select:
select id
  from tb_member
 where name = #{name}
   and email = #{email}

mapper.delete:
delete 
  from tb_member
 where id = #{id}
   and pass = #{pass}
